mapper.select:
select * from howdo_article
where title=#{article.title}
and content=#{article.content}
and user_id=#{article.userId}
and cate_id=#{article.cateId}

select ha.*,hu.id,hu.nickname,hu.head_pic,hu.signature from howdo_article ha,howdo_user hu where is_publish='Y' and enable_flag='Y' and hu.id=ha.user_id
and ha.cate_id=#{cateId}
order by last_update_date desc

select ha.*,hu.id,hu.nickname,hu.head_pic,hu.signature from howdo_article ha,howdo_user hu,howdo_collect hc
where is_publish='Y' and enable_flag='Y' and hu.id=ha.user_id and hc.art_id=ha.id and hc.user_id=#{userId}
order by last_update_date desc
